// CREATE IMAGE 
    public function testImageCreate()
    {
        $client = new Client();

        $form = $this->data('create');
        $client = new Client();
        $response = $client->post('http://localhost/api/image/create', [
            'headers' => [
                'Content-Type' => 'application/json',
            ],
            'body' => $form
        ]);
        $body = $response->getBody();

        $data = json_decode((string) $body);

        $this->assertNotNull($data, "The response is not a valid JSON string.");
        $this->assertEquals(200, $response->getStatusCode());

        $this->assertEquals("El archivo se guardó exitosamente", $data->Message);

        $this->assertNotEquals("El archivo se guardó exitosamente", $data->Message);
        
    }

 
